import React, { useState } from 'react';
import { Task } from "./Task";


const initialTodos = [
  { text: "Call the customer", complete: true },
  { text: "Send a contract", complete: false }
];

const App: React.FC = () => {
  const [todos, setTodos] = useState(initialTodos);

  const toggleTodo = selectedTodo => {
    const newTodos = todos.map(todo => {
      if(todo === selectedTodo) {
        return {
          ...todo,
          complete: !todo.complete
        }
      }
      return todo;
    })
  }

  return (
    <React.Fragment>
      <Task todo={todos[0]}/>
      <Task todo={todos[1]}/>
    </React.Fragment>
  )
};

